package com.crystalcg.gamedev.util.cache.domain;

/**
 * 讨伐战斗伤害补正实体
 * @author jinganyang
 *
 */
public class StaticBattleHurt {
	private int id;
	private String hurtNo;//伤害编号
	private int value;//伤害补正值,在计算时需要除100
	
	public int getId() {
		return id;
	}
	public String getHurtNo() {
		return hurtNo;
	}
	public int getValue() {
		return value;
	}
	public void setId(int id) {
		this.id = id;
	}
	public void setHurtNo(String hurtNo) {
		this.hurtNo = hurtNo;
	}
	public void setValue(int value) {
		this.value = value;
	}
}
