package com.crystalcg.gamedev.util.cache.domain;

/**
 * 聚贤阁等级信息表
 * 
 * @author xuzhongxing
 * 
 */
public class StaticCentrestage {
	private int level;// 当前聚贤阁级别
	private int rank1Num;// 能任命官职1的最大数目
	private int rank2Num;// 能任命官职2的最大数目
	private int rank3Num;// 能任命官职3的最大数目
	private int rank4Num;// 能任命官职4的最大数目
	private int rank5Num;// 能任命官职5的最大数目
	private int rank6Num;// 能任命官职6的最大数目
	private int rank7Num;// 能任命官职7的最大数目

	public int getLevel() {
		return level;
	}

	public int getRank1Num() {
		return rank1Num;
	}

	public int getRank2Num() {
		return rank2Num;
	}

	public int getRank3Num() {
		return rank3Num;
	}

	public int getRank4Num() {
		return rank4Num;
	}

	public int getRank5Num() {
		return rank5Num;
	}

	public int getRank6Num() {
		return rank6Num;
	}

	public int getRank7Num() {
		return rank7Num;
	}

	public void setLevel(int level) {
		this.level = level;
	}

	public void setRank1Num(int rank1Num) {
		this.rank1Num = rank1Num;
	}

	public void setRank2Num(int rank2Num) {
		this.rank2Num = rank2Num;
	}

	public void setRank3Num(int rank3Num) {
		this.rank3Num = rank3Num;
	}

	public void setRank4Num(int rank4Num) {
		this.rank4Num = rank4Num;
	}

	public void setRank5Num(int rank5Num) {
		this.rank5Num = rank5Num;
	}

	public void setRank6Num(int rank6Num) {
		this.rank6Num = rank6Num;
	}

	public void setRank7Num(int rank7Num) {
		this.rank7Num = rank7Num;
	}
}
