package com.crystalcg.gamedev.util.cache.domain;

import org.codehaus.jackson.annotate.JsonIgnore;

/**
 * 城防工事
 * 
 * @author xuzhongxing
 * 
 */
public class StaticDefenceworks {
	private String defenceworksNo;// 城防编号
	private String name;// 城防名
	private int produceTime;// 建造时间（s）
	private int needWood;// 需要木材
	private int needStone;// 需要石料
	private int needIronore;// 需要铁矿
	@JsonIgnore
	private String needItem;// 需要物品
	@JsonIgnore
	private int itemRate;// 物品城防兑换率
	private int atk;// 攻击
	@JsonIgnore
	private int atkType;// 攻击类型 1=普通 2=弓箭 3=投石
	@JsonIgnore
	private int atkRange;// 攻击范围 1=1 2=5 3=13
	private String description;// 描述
	public String getDefenceworksNo() {
		return defenceworksNo;
	}
	public void setDefenceworksNo(String defenceworksNo) {
		this.defenceworksNo = defenceworksNo;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public int getProduceTime() {
		return produceTime;
	}
	public void setProduceTime(int produceTime) {
		this.produceTime = produceTime;
	}
	public int getNeedWood() {
		return needWood;
	}
	public void setNeedWood(int needWood) {
		this.needWood = needWood;
	}
	public int getNeedStone() {
		return needStone;
	}
	public void setNeedStone(int needStone) {
		this.needStone = needStone;
	}
	public int getNeedIronore() {
		return needIronore;
	}
	public void setNeedIronore(int needIronore) {
		this.needIronore = needIronore;
	}
	public String getNeedItem() {
		return needItem;
	}
	public void setNeedItem(String needItem) {
		this.needItem = needItem;
	}
	public int getItemRate() {
		return itemRate;
	}
	public void setItemRate(int itemRate) {
		this.itemRate = itemRate;
	}
	public int getAtk() {
		return atk;
	}
	public void setAtk(int atk) {
		this.atk = atk;
	}
	public int getAtkType() {
		return atkType;
	}
	public void setAtkType(int atkType) {
		this.atkType = atkType;
	}
	public int getAtkRange() {
		return atkRange;
	}
	public void setAtkRange(int atkRange) {
		this.atkRange = atkRange;
	}
	public String getDescription() {
		return description;
	}
	public void setDescription(String description) {
		this.description = description;
	}
	
	
   
}
