package com.crystalcg.gamedev.util.cache.domain;

public class StaticImageResource {
	private int id;
	private String name;
	private int x;
	private int y;
	private int type;
	
	public int getId() {
		return id;
	}
	public String getName() {
		return name;
	}
	public int getX() {
		return x;
	}
	public int getY() {
		return y;
	}
	public int getType() {
		return type;
	}
	public void setId(int id) {
		this.id = id;
	}
	public void setName(String name) {
		this.name = name;
	}
	public void setX(int x) {
		this.x = x;
	}
	public void setY(int y) {
		this.y = y;
	}
	public void setType(int type) {
		this.type = type;
	}
}
