package com.crystalcg.gamedev.util.cache.domain;

/**
 * 打造奖励表
 * @author xuzhongxing
 *
 */
public class StaticProduceReward {

	private int materialLevel; // 材料等级
	private double qualityRate1; // 品质1（白色物品）的产品概率
	private double qualityRate2; // 品质2（绿色物品）的产品概率
	private double qualityRate3; // 品质3（蓝色物品）的产品概率
	private double qualityRate4; // 品质4（紫色物品）的产品概率
	private double qualityRate5; // 品质5（橙色物品）的产品概率
	private double qualityRate6; // 品质6（红色物品）的产品概率

	public int getMaterialLevel() {
		return materialLevel;
	}

	public double getQualityRate1() {
		return qualityRate1;
	}

	public double getQualityRate2() {
		return qualityRate2;
	}

	public double getQualityRate3() {
		return qualityRate3;
	}

	public double getQualityRate4() {
		return qualityRate4;
	}

	public double getQualityRate5() {
		return qualityRate5;
	}

	public double getQualityRate6() {
		return qualityRate6;
	}

	public void setMaterialLevel(int materialLevel) {
		this.materialLevel = materialLevel;
	}

	public void setQualityRate1(double qualityRate1) {
		this.qualityRate1 = qualityRate1;
	}

	public void setQualityRate2(double qualityRate2) {
		this.qualityRate2 = qualityRate2;
	}

	public void setQualityRate3(double qualityRate3) {
		this.qualityRate3 = qualityRate3;
	}

	public void setQualityRate4(double qualityRate4) {
		this.qualityRate4 = qualityRate4;
	}

	public void setQualityRate5(double qualityRate5) {
		this.qualityRate5 = qualityRate5;
	}

	public void setQualityRate6(double qualityRate6) {
		this.qualityRate6 = qualityRate6;
	}

}
