package com.crystalcg.gamedev.util.cache.domain;

/**
 * 官职信息表
 * 
 * @author xuzhongxing
 * 
 */
public class StaticRank {
	private String rankNo;// 官职编号
	private String rankName;// 官职的中文名称
	private int rankCommand;// 任命官职后给武将加成的统率
	private int needExploit;// 册封此官职需要的军功
	private String needItem;// 册封此官职需要的道具

	public String getRankNo() {
		return rankNo;
	}

	public String getRankName() {
		return rankName;
	}

	public int getRankCommand() {
		return rankCommand;
	}

	public int getNeedExploit() {
		return needExploit;
	}

	public String getNeedItem() {
		return needItem;
	}

	public void setRankNo(String rankNo) {
		this.rankNo = rankNo;
	}

	public void setRankName(String rankName) {
		this.rankName = rankName;
	}

	public void setRankCommand(int rankCommand) {
		this.rankCommand = rankCommand;
	}

	public void setNeedExploit(int needExploit) {
		this.needExploit = needExploit;
	}

	public void setNeedItem(String needItem) {
		this.needItem = needItem;
	}
}
