package com.crystalcg.gamedev.util.cache.domain;

/**
 * 兵种技能静态表
 * 
 * @author xuzhongxing
 * 
 */
public class StaticSoldierSkill {
	private String skillNo;// 技能编号
	private int skillLevel;// 技能等级
	private int effectType;// 特效类型
	private float probability;// 触发几率

	public String getSkillNo() {
		return skillNo;
	}

	public int getSkillLevel() {
		return skillLevel;
	}

	public int getEffectType() {
		return effectType;
	}

	public float getProbability() {
		return probability;
	}

	public void setSkillNo(String skillNo) {
		this.skillNo = skillNo;
	}

	public void setSkillLevel(int skillLevel) {
		this.skillLevel = skillLevel;
	}

	public void setEffectType(int effectType) {
		this.effectType = effectType;
	}

	public void setProbability(float probability) {
		this.probability = probability;
	}
}
