package com.crystalcg.gamedev.util.cache.domain;

import org.codehaus.jackson.annotate.JsonIgnore;

/**
 * 名城、国都
 * 
 * @author xuzhongxing
 * 
 */
public class StaticWorldCity {
	private int id;
	private int x;
	private int y;
	private String name;
	@JsonIgnore
	private int type;// 1:国都，2：名城
	private int country;// 所属国家（冗余）
	private int img;// 外观 6~11：国都，12~20：名城
	private int need_level;// 占领需要的联盟等级
	private String description;

	public int getId() {
		return id;
	}

	public int getX() {
		return x;
	}

	public int getY() {
		return y;
	}

	public String getName() {
		return name;
	}

	public int getType() {
		return type;
	}

	public int getCountry() {
		return country;
	}

	public int getImg() {
		return img;
	}

	public int getNeed_level() {
		return need_level;
	}

	public String getDescription() {
		return description;
	}

	public void setId(int id) {
		this.id = id;
	}

	public void setX(int x) {
		this.x = x;
	}

	public void setY(int y) {
		this.y = y;
	}

	public void setName(String name) {
		this.name = name;
	}

	public void setType(int type) {
		this.type = type;
	}

	public void setCountry(int country) {
		this.country = country;
	}

	public void setImg(int img) {
		this.img = img;
	}

	public void setNeed_level(int need_level) {
		this.need_level = need_level;
	}

	public void setDescription(String description) {
		this.description = description;
	}

}
